package ds.leetcode.basic;

public class RepeatedSubStringPattern {

    public static void main(String[] args) {

        String str = "abbbba";

        //Solution No 1 (Best and Optimize)
//        String s = str.substring(1,str.length()) + str.substring(0, str.length()-1);


//        System.out.println(s.contains(str));
//        String str = "";

        boolean value = isRepeatedSubString(str);
        System.out.println(value);
    }

    private static boolean isRepeatedSubString(String str) {

        if (str.isBlank() || str.length() == 1){
            return false;
        }

        StringBuilder subStr1 = new StringBuilder();
        int count = 0;
        int lengthOfString = str.length();

        if (lengthOfString % 2 == 0){
            String substring = str.substring(0, lengthOfString / 2);

            String s = substring + substring;

            return s.equals(str);
        }

        for (char c : str.toCharArray()) {

            subStr1.append(c);
            if (str.replace(subStr1,"").isBlank()){
                return true;
            }
            count++;

            if (count >= lengthOfString/2) {
                return false;
            }
        }
        return false;
    }
}
